import java.io.*;
import java.nio.charset.Charset;
import java.nio.charset.StandardCharsets;
import java.nio.file.Files;
import java.nio.file.Paths;
import java.util.Scanner;

public class Leet {

    static String readFile(String path, Charset encoding) throws IOException {
        byte[] encoded = Files.readAllBytes(Paths.get(path));
        return new String(encoded, encoding);
    }

    static String translator(String text) {
        if (text.contains("a")) {
            text = text.replace("a","4");
        }
        if (text.contains("b")) {
            text = text.replace("b","8");
        }
        if (text.contains("c")) {
            text = text.replace("c","<");
        }
        if (text.contains("d")) {
            text = text.replace("d","|)");
        }
        if (text.contains("e")) {
            text = text.replace("e","3");
        }
        if (text.contains("f")) {
            text = text.replace("f","|=");
        }
        if (text.contains("g")) {
            text = text.replace("g","9");
        }
        if (text.contains("h")) {
            text = text.replace("h","#");
        }
        if (text.contains("i")) {
            text = text.replace("i","1");
        }
        if (text.contains("j")) {
            text = text.replace("j","_|");
        }
        if (text.contains("k")) {
            text = text.replace("k","|<");
        }
        if (text.contains("l")) {
            text = text.replace("l","|_");
        }
		if (text.contains("m")) {
            text = text.replace("m","|\\/|");
        }
        if (text.contains("n")) {
            text = text.replace("n","|\\|");
        }
        if (text.contains("o")) {
            text = text.replace("o","0");
        }
        if (text.contains("p")) {
            text = text.replace("p","|2");
        }
        if (text.contains("q")) {
            text = text.replace("q","(,)");
        }
        if (text.contains("r")) {
            text = text.replace("r","|?");
        }
        if (text.contains("s")) {
            text = text.replace("s","$");
        }
        if (text.contains("t")) {
            text = text.replace("t","7");
        }
		if (text.contains("u")) {
            text = text.replace("u","|_|");
        }
        if (text.contains("v")) {
            text = text.replace("v","\\/");
        }
        if (text.contains("w")) {
            text = text.replace("w","\\/\\/");
        }
        if (text.contains("x")) {
            text = text.replace("x","><");
        }
        if (text.contains("y")) {
            text = text.replace("y","´/");
        }
        if (text.contains("z")) {
            text = text.replace("z","2");
        }
        return text;      
    }

    public static void main(String[] args) throws IOException {
        Scanner scanner = new Scanner(System.in);

        System.out.print("Kérem a fálj nevét: ");
        String fileName = scanner.nextLine();
        String fullFileContents =
                readFile(fileName,StandardCharsets.UTF_8);  
        System.out.println("File beolvasva.");
        fullFileContents = fullFileContents.toLowerCase();
        fullFileContents = translator(fullFileContents);
        System.out.println("File lefordítva.");

        PrintWriter writer = new PrintWriter("output.txt", "UTF-8");
        writer.print(fullFileContents);
        writer.close();
        System.out.println("A fordított szöveg az
                            output.txt-ben megtalálható.");

        scanner.close();
    }
}

static String readFile(String path, Charset encoding) throws IOException {
    byte[] encoded = Files.readAllBytes(Paths.get(path));
    return new String(encoded, encoding);
}

static String translator(String text) {
    if (text.contains("a")) {
        text = text.replace("a","4");
    }
}

public static void main(String[] args) throws IOException {
    Scanner scanner = new Scanner(System.in);

    System.out.print("Kérem a fálj nevét: ");
    String fileName = scanner.nextLine();
    String fullFileContents =
            readFile(fileName,StandardCharsets.UTF_8);  
    System.out.println("File beolvasva.");
    fullFileContents = fullFileContents.toLowerCase();
    fullFileContents = translator(fullFileContents);
    System.out.println("File lefordítva.");

    PrintWriter writer = new PrintWriter("output.txt", "UTF-8");
    writer.print(fullFileContents);
    writer.close();
    System.out.println("A fordított szöveg az
                        output.txt-ben megtalálható.");

    scanner.close();
}

import java.awt.*;
import java.awt.event.ActionEvent;
import java.awt.event.ActionListener;

import javax.swing.*;

public class Login {

    public static void main(String[] args) {

        //Create, set buttons

        //Login button
        JButton login = new JButton("Login");
        login.setBounds(50, 300, 275, 50);

        //Exit button
        JButton exit = new JButton("Exit");
        exit.setBounds(375, 300, 275, 50);
        exit.addActionListener(new ActionListener() {
            @Override
            public void actionPerformed(ActionEvent e) {
                System.exit(0);
            }
        });

        //Create, set labels

        //User name label
        JLabel username = new JLabel("Username:");
        username.setBounds(50, 50, 200, 75);
        username.setFont(new Font("Arial", Font.PLAIN, 40));

        //Password label
        JLabel password = new JLabel("Password:");
        password.setBounds(50, 150, 200, 75);
        password.setFont(new Font("Arial", Font.PLAIN, 40));

        //Create, set text fields

        //User text field
        JTextField userText = new JTextField();
        userText.setBounds(275, 55, 375, 60);

        //Pass text field
        JTextField passText = new JTextField();
        passText.setBounds(275, 155, 375, 60);

        //Create, set panel
        JPanel panel = new JPanel();

        Dimension dim = Toolkit.getDefaultToolkit().getScreenSize();

        //Set panel
        int width = 700;
        int height = 400;
        panel.setBounds((dim.width-width)/2,
                            (dim.height-height)/2, width, height);
        panel.setLayout(null);

        //Add buttons,labels,text fields to panel
        panel.add(login);
        panel.add(exit);
        panel.add(username);
        panel.add(password);
        panel.add(userText);
        panel.add(passText);

        //Create, set frame
        JFrame frame = new JFrame();
        frame.setExtendedState(Frame.MAXIMIZED_BOTH);
        //set width, height full
        frame.setUndecorated(true); // disable decorations on frame
        frame.getContentPane().setBackground(new Color(87, 147, 77));
        frame.setVisible(true); //show window
        frame.setLayout(null);

        //Add panel to frame
        frame.add(panel);
    }
}

